pragma solidity ^0.4.23;
contract Auction {
  address public manager;
  uint public latestBid;
  uint public initBid;
  address public latestBidder;
  bool public isBidding;
  uint public timeBid;

  constructor() public {
    manager = msg.sender;
    isBidding = false;
  }

  function auction(uint bid) public {
    initBid = bid * 1 ether; //1000000000000000000;
    isBidding = true;
    latestBid = initBid;
    timeBid = 0;
  }

  function bid() public payable {
    require(msg.value > latestBid);

    if (latestBidder != 0x0) {
      latestBidder.transfer(latestBid);
    }
    latestBidder = msg.sender;
    latestBid = msg.value;
    timeBid = 0;
  }

  function increaseTimeBid() restricted public {
      timeBid = timeBid + 1;
      if (timeBid == 3) {
        finishAuction();
      }
  }

  function finishAuction() restricted public {
    manager.transfer(address(this).balance);
    isBidding = false;
  }

  function cancelAuction() restricted public {
    if (latestBidder != 0x0) {
      latestBidder.transfer(address(this).balance);
      isBidding = false;
    }
   }

  modifier restricted() {
    require(msg.sender == manager);
    _;
  }
}
